mapper.update:
UPDATE TB_BABY
SET BABY_INDEX = #{babyIndex}

    , BABY_NICK_NAME = #{babyNickName}


    , BABY_BIRTHDAY = #{babyBirthday}


    , BABY_GENDER_CODE = #{babyGenderCode}

WHERE BABY_INDEX = #{babyIndex}

UPDATE /*deleteBaby*/
       TB_BABY
   SET BABY_DELETE_STATE = #{babyDeleteState}
 WHERE BABY_INDEX = #{babyIndex}

UPDATE /*changeBabyLightIndex*/
       TB_BABY
   SET LIGHT_INDEX = #{newLightIndex}
 WHERE LIGHT_INDEX = #{oldLightIndex}

UPDATE /*updateBabyImagePath*/
       TB_BABY
   SET BABY_IMAGE_PATH = #{babyImagePath}
 WHERE BABY_INDEX = #{babyIndex}
